package sbml.test;
import java.math.*;

public class TestResultDetails {
	// the TestResultDetails class has 4 fields
	private int result;
	private String testname;
	private String description;
	private String warnings;
	private String plot;
	private String html;
	

	// the TestResultDetails class has one constructor
	public TestResultDetails(int output, String name, String desc, String warning, String plot_path, String html_path) {
		result = output;
		testname = name;
		description = desc;
		warnings = warning;
		plot = plot_path;
		html = html_path;
	}

	// the TestResultDetails class has 4 methods
	public void setResult(int newValue) {
		result = newValue;
	}
	public void setTestname(String newValue) {
		testname = newValue;
	}
	public void setWarnings(String newValue) {
		warnings = newValue;
	}
	public void setDescription(String newValue) {
		description = newValue;
	}
	public void setPlot(String newValue) {
		plot = newValue;
	}
	public void setHtml(String newValue) {
		html = newValue;
	}
	
	public int getResult() {
		return result;
	}
	public String getTestname() {
		return testname;
	}
	public String getWarnings() {
		return warnings;
	}
	public String getPlot() {
		return plot;
	}
	public String getHtml() {
		return html;
	}
	public String getDescription() {
		return description;
	}
		
}
